CREATE TABLE users (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, username TEXT NOT NULL, hash TEXT NOT NULL, is_admin BOOLEAN NOT NULL DEFAULT 0);
CREATE UNIQUE INDEX username on users (username);
CREATE TABLE classes (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, code TEXT NOT NULL, name TEXT NOT NULL);
CREATE TABLE psets (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, class_id INTEGER NOT NULL, name TEXT NOT NULL, description TEXT NOT NULL, FOREIGN KEY (class_id) REFERENCES class(id));
CREATE TABLE feedback (id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, user_id INTEGER NOT NULL, pset_id INTEGER NOT NULL, rating INTEGER NOT NULL, hours_spent INTEGER NOT NULL, difficulty INTEGER NOT NULL, enjoyment TEXT NOT NULL, comments TEXT NOT NULL, FOREIGN KEY (user_id) REFERENCES user(id), FOREIGN KEY (pset_id) REFERENCES pset(id));